import sys
from collections import deque

def solve():
    input = sys.stdin.readline
    H, W, T = map(int, input().split())
    grid = [input().strip() for _ in range(H)]
    candies = []
    for i in range(H):
        for j in range(W):
            if grid[i][j] == 'S':
                sx, sy = i, j
            elif grid[i][j] == 'G':
                gx, gy = i, j
            elif grid[i][j] == 'o':
                candies.append((i, j))
    n = len(candies)
    INF = 10**9
    dirs = [(-1,0),(1,0),(0,-1),(0,1)]
    def bfs(start):
        dist = [-1]*(H*W)
        q = deque()
        idx = start[0]*W + start[1]
        dist[idx] = 0
        q.append(start)
        while q:
            x,y = q.popleft()
            d = dist[x*W+y]
            for dx,dy in dirs:
                nx,ny = x+dx, y+dy
                if 0<=nx<H and 0<=ny<W and grid[nx][ny] != '#':
                    nidx = nx*W+ny
                    if dist[nidx] == -1:
                        dist[nidx] = d+1
                        q.append((nx,ny))
        return dist
    # BFS from S
    distS_all = bfs((sx,sy))
    distS_to_G = distS_all[gx*W+gy]
    if distS_to_G == -1:
        distS_to_G = INF
    distS = [INF]*n
    for i,(x,y) in enumerate(candies):
        d = distS_all[x*W+y]
        if d != -1:
            distS[i] = d
    # BFS from G
    distG_all = bfs((gx,gy))
    distG = [INF]*n
    for i,(x,y) in enumerate(candies):
        d = distG_all[x*W+y]
        if d != -1:
            distG[i] = d
    # Distances between candies
    dist = [[INF]*n for _ in range(n)]
    for i,(x,y) in enumerate(candies):
        d_all = bfs((x,y))
        for j,(x2,y2) in enumerate(candies):
            if i==j: continue
            d = d_all[x2*W+y2]
            if d != -1:
                dist[i][j] = d
    # DP
    size = 1<<n
    dp = [ [INF]*n for _ in range(size) ]
    for i in range(n):
        if distS[i] < INF:
            dp[1<<i][i] = distS[i]
    for mask in range(1, size):
        notmask = ((1<<n)-1) ^ mask
        sub = mask
        while sub:
            i_bit = sub & -sub
            i = (i_bit.bit_length() - 1)
            sub -= i_bit
            cur = dp[mask][i]
            if cur >= INF: continue
            sub2 = notmask
            while sub2:
                j_bit = sub2 & -sub2
                j = (j_bit.bit_length() - 1)
                sub2 -= j_bit
                nd = cur + dist[i][j]
                if nd < dp[mask | (1<<j)][j]:
                    dp[mask | (1<<j)][j] = nd
    ans = -1
    if distS_to_G <= T:
        ans = 0
    for mask in range(1, size):
        best = INF
        sub = mask
        while sub:
            i_bit = sub & -sub
            i = (i_bit.bit_length() - 1)
            sub -= i_bit
            cur = dp[mask][i]
            if cur >= INF: continue
            total = cur + distG[i]
            if total < best:
                best = total
        if best <= T:
            cnt = mask.bit_count()
            if cnt > ans:
                ans = cnt
    print(ans)

if __name__ == "__main__":
    solve()

class Solution:
    def minimumTimeToInitialState(self, word: str, k: int) -> int:
        n = len(word)
        # Compute prefix function (KMP)
        pi = [0] * n
        for i in range(1, n):
            j = pi[i-1]
            while j > 0 and word[i] != word[j]:
                j = pi[j-1]
            if word[i] == word[j]:
                j += 1
            pi[i] = j

        # Gather all border lengths (excluding full length)
        borders = set()
        l = pi[n-1]
        while l > 0:
            borders.add(l)
            l = pi[l-1]
        borders.add(0)  # empty border

        INF = 10**9
        min_t = INF
        for l in borders:
            if l == n:
                continue
            diff = n - l
            if diff % k == 0:
                t = diff // k
                if t > 0:
                    min_t = min(min_t, t)

        # If no border gives a valid t, we can always use ceil(n/k)
        t0 = (n + k - 1) // k
        if min_t == INF:
            return t0
        return min(min_t, t0)

