from typing import List
from collections import deque

class Solution:
    def maxSelectedElements(self, nums: List[int]) -> int:
        a = sorted(nums)
        n = len(a)
        c = [a[i] - i for i in range(n)]

        maxDeque = deque()  # indices for max c
        minDeque = deque()  # indices for min c (we will add +1 when needed)
        l = 0
        ans = 0

        for r in range(n):
            # maintain maxDeque for c
            while maxDeque and c[maxDeque[-1]] < c[r]:
                maxDeque.pop()
            maxDeque.append(r)

            # maintain minDeque for c (we will use c[idx] + 1)
            while minDeque and c[minDeque[-1]] + 1 > c[r] + 1:
                minDeque.pop()
            minDeque.append(r)

            # shrink window while invalid
            while l <= r and c[maxDeque[0]] > c[minDeque[0]] + 1:
                l += 1
                if maxDeque and maxDeque[0] < l:
                    maxDeque.popleft()
                if minDeque and minDeque[0] < l:
                    minDeque.popleft()

            if l <= r:
                ans = max(ans, r - l + 1)

        return ans

