class Solution:
    def minOrAfterOperations(self, nums: List[int], k: int) -> int:
        n = len(nums)
        need_segments = n - k  # at least this many segments
        mask = (1 << 30) - 1
        ans = 0
        for b in range(29, -1, -1):
            segments = 0
            cur_and = mask
            for x in nums:
                cur_and &= x
                if ((cur_and >> b) & 1) == 0:
                    segments += 1
                    cur_and = mask
            # cur_and with bit 1 at end is ignored (no new segment counted)
            if segments < need_segments:
                ans |= (1 << b)
        return ans

from typing import List
from collections import Counter

class Solution:
    def maximumLength(self, nums: List[int]) -> int:
        freq = Counter(nums)
        ans = 1  # at least one element can always be chosen

        # Handle base 1 separately: pattern is all 1's, length must be odd
        c1 = freq.get(1, 0)
        if c1:
            ans = max(ans, c1 if c1 % 2 == 1 else c1 - 1)

        limit = 10**9
        for x in freq:
            if x == 1:
                continue
            # Build the list of powers x^(2^i) until exceeding limit
            exps = []
            val = x
            while val <= limit:
                exps.append(val)
                # Prevent overflow when squaring
                if val > limit // val:
                    break
                val *= val

            # Find the maximum m such that for all i < m we have at least 2 copies,
            # and for i = m we have at least 1 copy
            m = -1
            for idx, v in enumerate(exps):
                cnt = freq.get(v, 0)
                if cnt >= 2:
                    continue
                else:
                    if cnt >= 1:
                        m = idx
                    else:
                        m = idx - 1
                    break
            if m == -1:  # all exponents had at least 2 copies
                m = len(exps) - 1

            length = 2 * m + 1
            if length > ans:
                ans = length

        return ans
